INSERT INTO `gram_topics` (`Title`, `Level`, `ImageURL`, `Description`) VALUES ("Reported Speech","B2","https://lh3.googleusercontent.com/proxy/p389kQ03d7PhDXMZr3kIlGSv6SZkzMh8xYEbAULlKO9VSPIA9GFoStspb9LLi66rvH3reRb8aScitmW33vAmhkZF2wBMq0kxulbGW3fOnCBE6iKlcw","Reported speech is when we tell someone what another person said. To do this, we can use direct speech or indirect speech");
INSERT INTO `gram_topics` (`Title`, `Level`, `ImageURL`, `Description`) VALUES ("Used to/Would","B2","https://i0.wp.com/teachers.onlineenglishexpert.com/wp-content/uploads/2014/02/Used-To-And-Would-Esl-Lesson-Plan.jpg?fit=700%2C400&ssl=1&w=640","But we use 'used to' for any extended action or situation in the past. 'Would' is only good for actions or situations that were repeated many times");
INSERT INTO `gram_topics` (`Title`, `Level`, `ImageURL`, `Description`) VALUES ("Wish/If only","B2","https://www.englishbix.com/wp-content/uploads/2020/02/Conditionals-I-Wish-or-If-Only-Sentence-Examples--1024x576.jpg", "We can use wish/if only + a past perfect form to talk about something we would like to change about the past");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("1","Các dạng thức của động từ be","I am... <br>SHe/He/It is... <br>We/They are...","Dùng để cung cấp thông tin hoặc bày tỏ cảm xúc","Chỉ áp dụng với thì hiện tại đơn, các cách áp dụng khác của be sẽ được mô tả chi tiết trong các bài sau");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("1","Khẳng định","S + be ...","Thể hiện sự tồn tại, trạng thái hoặc đặc điểm của người hoặc vật");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("1","Phủ định","S + be not ...","Dùng bày tỏ việc không tồn tại trạng thái hoặc đặc điểm đó");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("1","Câu hỏi","Be + S ... ?","Dùng để đặt câu hỏi về việc liệu chủ thể có tồn tại trạng thái hoặc đặc điểm nào đó không");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("2","HERE và THERE","Đứng ở cuối câu","Here Để chỉ về nơi đang nói tới (ở đây) <br>There để chỉ về nơi khác (ở đó)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("2","Chủ ngữ?","Sử dụng từ chỉ nơi chốn","Các cách dùng sai: <br>Here is terrible <br>Did you like here?","Không được dùng here there như danh từ thường hay chủ ngữ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("3","Sở thích lâu dài","S + like + V-ing","Dùng để nói về sở thích, đam mê cá nhân, mang tính lâu dài và hưởng thụ. Bạn thích làm việc đó để có được niềm vui, sự thư giãn (dù việc đó có thể không đem lại lợi ích mà còn gây hại nhưng bạn vẫn thích)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("3","Sở thích nhất thời","S + like + to + V","Nói về một sở thích nhất thời, bộc phát, không mang tính lâu dài <br>Nói về một việc mà bạn nên làm vì cảm thấy nó tốt đẹp, đúng đắn, đúng theo lẽ thường và tiêu chuẩn xã hội cũng như mang lại lợi ích cho bạn <br>Dùng để nói về sự lựa chọn giữa hai hoặc nhiều việc khác nhau. Bạn thích việc này hơn việc kia (phần “việc kia” có thể lược bỏ nhưng người ta vẫn hiểu bạn đang lựa chọn)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("4","Đại từ chủ ngữ","I, You, He, She, It, We, Them","Đây là những đại từ được sử dụng thay thế cho người hay vật cụ thể, chúng được biến đổi theo sự khác nhau của giới tính, số lượng, trường hợp, và ngôi thứ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("4","Đại từ tân ngữ","me, you, him, her, it, us, them","Dùng làm tân ngữ của động từ, chỉ rõ người nói đang nói tới ai");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("5","SỞ HỮU CÁCH (POSSESSIVE ‘S)","Người làm chủ + ‘s + vật/ người thuộc quyền sở hữu (tức là thuộc về người đó)","Sở hữu cách được dùng để diễn đạt sự sở hữu");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("6","Khẳng định","<b>S + V-s-es</b> (S = He/She/It) <br><b>S + V-1</b> (S = I/We/You/They)","Thói quen hoặc hành động lặp đi lặp lại ở hiện tại <br>Chân lí, sự thật hiển nhiên <br>Nhận thức, cảm giác hoặc tình trạng ở hiện tại <br>Việc sẽ xảy ra trong tương lai. Cách này để nói về thời gian biểu hoặc những kế hoạch đã cố định thời gian","Cách dùng hiện tại đơn cho tương lai chỉ phổ biến ở Mỹ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("6","Phủ định","Be thêm not<br>Don’t/dosen’t + bare-inf","Dùng trong các tình huống tương tự ở hiện tại và mang sắc thái phủ định - không....");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("6","Nghi vấn","Be + S …? <br>Do/Does + S + bare-inf …?","Dùng để đặt câu hỏi trong các tình huống tương tự ở hiện tại - Có....không");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("7","This","This + is + danh từ số ít (gần chúng ta)","Chúng ta sử dụng “this” để nói về con người/sự vật/nơi chốn ở số ít và ở gần chúng ta");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("7","That","That + is + danh từ số ít (ở xa chúng ta)","Chúng ta sử dụng “that” để nói về con người/sự vật/nơi chốn ở số ít và ở xa chúng ta");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("7","These","These + are + danh từ số nhiều (gần chúng ta)","Chúng ta sử dụng “these” để nói về  con người/sự vật/nơi chốn ở số nhiều và ở gần chúng ta");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("7","Those","Chúng ta sử dụng “those” để nói về  con người/sự vật/nơi chốn ở số nhiều và ở xa chúng ta","Those + are + danh từ số nhiều (ở xa chúng ta)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`, `Note`) VALUES ("8","Was/Were","S + was/were + noun/adj","Về bản chất, was/were đều là động từ tobe được sử dụng ở dạng quá khứ nhưng cách sử dụng hai động từ này trong ngữ cảnh khác nhau","Câu điều kiện không có thật hoặc mong  ước chỉ dùng were cho mọi chủ ngữ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("9","Mạo từ xác định","The + N <br>The + adj","Sự vật, sự việc kể đến hai lần <br>Những thứ duy nhất <br>Kết hợp với tính từ để chỉ một tập thể (the poor - Những người nghèo nói chung)<br>Đứng trước tên riêng <br>Quốc tịch <br>Một số quốc gia hợp nhất (The United States, The United Kingdoms)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("9","Mạo từ không xác định","A + danh từ đếm được số ít <br>An + danh từ đếm được số ít bắt đầu bằng nguyên âm (a, e, o, u, i)","Trước sự vật, sự việc được kể đến lần đầu tiên <br>Chỉ nghề nghiệp <br>Các cụm từ/ từ chỉ lượng: a pair of/ a thousand/ a little/ a few <br>Có nghĩa là “một”");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("10","Trạng từ (phó từ)"," S + V + adv (adv = adj-ly) <br>Một số trạng từ bất quy tắc gồm good -> well; hard -> hard; fast -> fast","Trạng từ bổ nghĩa cho động từ, cho một tính từ hoặc cho một trạng từ khác. Nó trả lời cho câu hỏi How?");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("11","Cụm trạng từ chỉ mức độ (Adverbial phrases of degree/extent)","Cụm trạng từ chỉ mức độ thể hiện cường độ, mức độ hoặc trọng tâm của hành động. Trong đó, trạng từ chỉ mức độ làm rõ nghĩa cho trạng từ đi kèm sau đó","<strong>Tham khảo một số trạng từ chỉ mức độ sắp xếp theo thứ tự giảm dần:</strong> <br>completely, totally, absolutely, entirely, quite <br>very, extremely, really, awfully, terribly <br>rather, fairly, quite, pretty, somewhat <br>a little, a bit, slightly <br>hardly, scarcely, at all");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("11","Cụm trạng từ chỉ khả năng (Adverbial phrases of probability)","Cụm trạng từ chỉ khả năng thể hiện mức độ chắc chắn của chúng ta về một sự vật, sự việc nào đó","<strong>Tham khảo một số trạng từ chỉ khả năng</strong> <br>certainly; conceivably; definitely; doubtless; indeed; of course; obviously;  really; surely; truly; undoubtedly <br>likely; maybe; perhaps; possibly; probably; unlikely");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("12","Can","S + can + V-1 ","Để chỉ một việc có khả năng xảy ra <br>Nói về con người có năng lực làm việc gì đó <br>Trong câu hỏi dùng để xin phép","Can dùng để nói về hiện tại hoặc tương lai");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("12","Could","S + could + V-1","“Could” cũng được dùng trong mệnh đề phụ do chi phối của động từ quá khứ ở mệnh đề chính <br>“Could” (nghĩa “có lẽ”) dùng để chỉ một việc có lẽ sẽ diễn ra trong “tương lai” nhưng “chưa chắc chắn” <br>“could” đặc biệt sử dụng với những động từ sau: See (nhìn), hear (nghe), smell (ngửi), taste (nếm), feel (cảm thấy), remember (nhớ), understand (hiểu)","Could có thể hiểu như dạng quá khứ của can");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("13","Comparison of Equality","AS…….AS……. <br>NOT AS……..AS…………./ NOT SO………………AS……………","So sánh 2 đối tượng bằng nhau hoặc không bằng nhau");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("13","Forming Comparative","S + be + tính từ ngắn-er + than + O <br>S + be + more + tính từ dài + than + O","So sánh hơn giữa 2 đối tượng","good -> better, bad -> worse, far -> farther/further, little -> less, many/much -> more");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("13","Superlative Adjectives","S + be + tính từ dài-est + (N) <br>S + be + the most + tính từ dài + (N)","Nói về thứ đứng nhất trong nhóm từ 3 đối tượng so sánh trở lên","good -> the best, bad -> the worst, far -> farthest, little -> the least, many/much -> the most");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("14","Câu điều kiện loại 1"," If + S + V (present simple), S + V (future simple)","Loại câu này mô tả các sự kiện sẽ xảy ra hoặc có khả năng xảy ra trong hiện tại hoặc tương lai","Có thể dùng các thì hiện tại khác trong câu điều kiện");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("15","GERUND","V-ing (thêm -ing vào động từ thường)","Có thể làm chủ ngữ, bổ ngữ, tân ngữ của động từ khác <br>Sau các động từ: enjoy, avoid, admit, appreciate (đánh giá cao), mind (quan tâm, ngại), finish, practice, suggest, postpone (hoãn lại), consider (xem xét), hate, admit (thừa nhận), like, love, deny (phủ nhận), detest (ghét), keep (tiếp tục), miss (bỏ lỡ), imagine (tưởng tượng), mention, risk, delay (trì hoãn) <br>Sau các cụm động từ: cant’ help (không thể không), can’t bear / can’t stand (không thể chịu được), be used to, get used to, look forward to, it’s no use / it’s no good (không có ích lợi gì), be busy, be worth (đáng giá) <br>Sau giới từ: in, on, at, from, to, about … <br>Sau các liên từ: after, before, when, while, since,…");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("15","INFINITIVE","To-infinitive / Infinitive with to: Động từ nguyên mẫu có “to”","Dùng làm chủ ngữ, bổ ngữ, tân ngữ <br>Sau các động từ: Agree , appear, afford, ask, demand, expect, hesitate, intend, invite, want, wish, hope, promise, decide, tell, refuse, learn, fail (thất bại), plan, manage, pretend (giả vờ), remind, persuade, encourage, force, order, urge (thúc giục), seem, tend, threaten,…");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("16","Be going to","S + be(am/is/are) + going to + V-inf","Mô tả quyết định hay kế hoạch đã được dự định trong tương lai <br>Dùng was/were going to để mô tả dự định trong quá khứ nhưng không thực hiện được <br>Dùng để dự đoán");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("17","CẤU TRÚC VÀ CÁCH DÙNG HAVE TO","Subject + have to + Verb (infinitive)","Cách diễn đạt này mang tính cấp thiết nhất. Người nói xem việc này là một nhiệm vụ, do quy định nào đó bắt buộc","Have got to có nghĩa giống have to nhưng chỉ được sử dụng ở thì hiện tại");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("18","Hỏi tính chất","How + be + noun? (Cái đó như thế nào?)","Trong trường hợp này, how được sử dụng để đặt câu hỏi về tính chất của một sự vật, sự việc, hiện tượng nào đó trong cuộc sống");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("18","Hỏi tần suất","How often …?","Câu hỏi với how often để hỏi tần suất, mức độ thường xuyên của hành động được thực hiện bởi ai đó");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("18","Hỏi thời gian","How long …? (Bao lâu)","Câu hỏi với how long được dùng để hỏi khoảng thời gian của hành động, sự việc");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("18","Hỏi kèm với tính từ cụ thể","How + adj + Noun?","Câu hỏi với how + adj được sử dụng rất phổ biến trong cả giao tiếp và ngữ pháp tiếng Anh. How có thể kết hợp với nhiều tính từ khác nhau để đặt câu hỏi về thông tin chi tiết của một vật, sự việc nào đó");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("18","Hỏi nhiều ít","How many/How much …?","Câu hỏi với how many/how much để hỏi về số lượng của danh từ đếm được, danh từ không đếm được trong tiếng Anh");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("18","Còn về ... thì sao?","How about + V-ing?","Cấu trúc này được dùng làm câu đề nghị người khác làm một việc gì đó. Trong trường hợp này, cấu trúc how about = what about");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("18","Làm như thế nào","How do + S + V?","Câu hỏi với cấu trúc how do + S + V? được dùng để hỏi cách thức làm một việc gì đó");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("19","Imperative","Bắt đầu bằng một động từ, chủ ngữ được ẩn đi nhưng nó ngầm đề cập đến ngôi thứ 2 (You)","Đưa ra mệnh lệnh (Commands), Yêu cầu (requests) và đề nghị <br>Cảnh báo (warnings) hoặc sự nhắc nhở (reminders)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("20","Past Continuous Tense"," S + was/were + V-ing","Diễn đạt hành động đang xảy ra tại một thời điểm trong quá khứ thì có hành động khác chen vào<br>Hành động lặp đi lặp lại trong quá khứ và làm phiền đến người khác");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("21","Khẳng định","S + was/were + adj/N <br> S + V-2,ed","Diễn tả một hành động, sự việc đã diễn ra tại một thời điểm cụ thể, hoặc một khoảng thời gian trong quá khứ và đã kết thúc hoàn toàn ở quá khứ <br>Diễn tả một hành động lặp đi lặp lại trong quá khứ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("21","Phủ định","S + wasn't/weren't + N/adj <br>S + didn't + V-inf","Dùng trong các tình huống như đã kể trên nhưng ở thể phủ định");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("21","Nghi vấn","Was/Were + S + N/adj ? <br>Did + S + V-inf ?","Dùng để đặt câu hỏi trong các tình huống như đã kể trên");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("22","Khẳng định","S +  be + V-ing","Nói về thứ đang diễn ra ngay lúc nói <br>Tương lai gần, diễn ra theo kế hoạch <br> Hành động lặp đi lặp lại gây khó chịu","Hành động khác với mọi ngày (dùng phổ biến ở Anh)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("22","Phủ định","S + be-(not) + V-ing","Dùng trong các tình huống tương tự ở hiện tại và mang sắc thái phủ định - đang không....");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("22","Nghi vấn","Be + S + V-ing ?","Dùng để đặt câu hỏi trong các tình huống tương tự ở hiện tại - Có đang....không");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("23","Present Continous for future","S + am/is/are V-ing","Mặc dù có hình thức là thì hiện tại đơn nhưng trong một số hoàn cảnh người nói muốn nói về một dự định trong tương lai đã được lên kế hoạch từ trước <br>Một tiên đoán tương lai mà người nói có căn cứ và gần như tin chắc nó sẽ xảy ra");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("24","Present Perfect","(+): S + have/has + V-3 <br>(-): S + have/has not + V-3 <br>(?): Have/Has + S + V-3","Một hành động bắt đầu trong quá khứ và có thể xảy ra ở hiện tại <br>Một hành động bắt đầu trong quá khứ và kéo dài đến hiện tại (thường người nói sẽ không nói về thời điểm cụ thể)");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("25","Should","S + “Should”/ “Should not”+ Verb","“should” mang tính gợi ý, biểu đạt quan điểm cá nhân. “Should” thường được dùng cho các câu phủ định và câu hỏi");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("25","Ought to","S + “Ought to”/ “Ought not to” + Verb","“ought to” là một lời khuyên mang tính chắc chắn hơn “should”, giải pháp này đưa ra khi không còn cách giải quyết khác. Ngoài ra, “ought to” còn nói về nhiệm vụ hoặc luật lệ bắt buộc phải làm");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("26","Some/Any","Some/Any + N-nhiều/N-không đếm được","Dùng trong câu khẳng định <br>Some cũng có thể dùng trong câu hỏi <br>Any dùng trong câu phủ định hoặc câu hỏi");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("26","Much/Many","Much + N-không đếm được <br>Many + N-nhiều","Thường dùng trong câu hỏi và câu phủ định, câu khẳng định được dùng ít hơn");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("27","Chủ ngữ","Question word + V + O?","Các Wh-question được sử dụng như chủ ngữ chính trong câu, điền câu trả lời vào câu hỏi sẽ có được câu khẳng định ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("27","Tân ngữ","Question word + Auxiliary + S + V + (O) ?","Các Wh-question được sử dụng như tân ngữ hoặc túc từ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("28","Tương lai đơn","(+) S + will + V <br>(-) S + will not/won’t +V <br>(?) Will/Won’t + S + V?","Diễn tả sự việc sẽ xảy ra trong tương lai mà không xác định thời gian <br>Lời hứa hoặc quyết định nhất thời trong lúc nói <br>Đưa ra lời mời và lời đề nghị");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("29","Zero Conditional","If + S + V (present simple), S + V (present simple)","Loại câu này mô tả các sự việc luôn luôn đúng. Chúng ta có thể dùng when / if trong loại câu này <br>Có thể dùng cấu trúc mệnh lệnh trong mệnh kết quả; chỉ dùng động từ nguyên mẫu không có to chứ không có chủ ngữ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("30","Cấu trúc would you like"," Would you like + Noun? <br>Would you like + to verb (infinitive)?","Dùng để đưa ra lời đề nghị hoặc hỏi về ý thích của người khác một cách rất trang trọng và lịch sự <br><strong>Chấp nhận lời mời: </strong>Absolutely hoặc Yes, I’d love to <br><strong>Từ chối lời mời: </strong>I would love to but I cannot hoặc No, thank you so much");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("31","Both (of)","Both nghĩa là “cả hai”","Both có thể được dùng làm đại từ, chỉ 2 vật mà ta đã nhắc tới","Both/Both of không khác nhau nhiều về nghĩa mặc dù Both of phổ biến hơn trong tiếng Anh Mỹ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("31","Both X and Y","Both + Adjective + and + Adjective","Cụm từ này tương đương với Not only X but also Y (không chỉ X mà còn cả Y) <br>Both… and… có thể dùng với danh từ số ít: Both + Noun + and + Noun <br>Both cũng có thể dùng với danh từ số nhiều: Both + plural noun");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("31","Neither (of) và Neither X nor Y","Neither + singular nound <br>Neither of + determiner + plural noun","Neither nghĩa là “không cái này mà cũng không cái kia”. Neither là từ mang nghĩa phủ định và đi kèm với động từ số ít ở thể khẳng định");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("31","Either","...not.....either... = neither","Có thể dùng Either với động từ phủ định để thay thế Neither với động từ khẳng định");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("32","Câu điều kiện loại 2","If + S + Were/ weren’t/ V2/ed, S + Would/could/… + V_inf","Dùng để đưa ra một giả thuyết không thể xảy ra ở thời điểm hiện tại");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("33","Câu điều kiện loại 3","If + S + Had + V3/Ved, S + would/ could…+ have + V3/Ved","Điều kiện không có thật trong quá khứ <br>Điều kiện không thể xảy ra trong quá khứ, chỉ mang tính ước muốn trong quá khứ, một giả thiết trái ngược với những điều đã xảy ra");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("34","Câu hỏi gián tiếp/ Câu hỏi phức","Dạng câu chứa ý hỏi: Subject + verb ( phrase) + question word + subject + verb <br>Dạng câu hỏi: Auxiliary + S + V + question word + S + V?","Câu có hai mệnh đề nối với nhau bằng một từ nghi vấn (question word). Question word có thể là một từ hoặc cụm từ như: whose noun, how many, how much, how long, how often, what time, what kind.");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("35","Tương lai tiếp diễn","khẳng định: S + will + be + V-ing <br>S + will + not + be + V-ing <br>Will + S + be + V-ing ? - Yes, S + will/No, S + won’t","Hành động sẽ diễn ra và kéo dài liên tục suốt một khoảng thời gian ở tương lai <br>Hành động sẽ xảy ta như một phần trong kế hoạch hoặc một phần trong thời gian biểu");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("36","So...That ( Quá ... đến nỗi )","S + be + so + adj + that + S + V","Cấu trúc So that này cũng được dùng với danh từ, trong câu xuất hiện so many/ much/ little…. <br>S + V + so + much/little + uncountable noun + that + S + V");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("36","Such That","S + V + such + (a/an) + adj + noun + that + S + V","Cấu trúc So that có ý nghĩa tương tự với cấu trúc Such that nên chúng mình có thể đổi qua đổi lại giữa hai cấu trúc này đấy");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("36","Too","S + to be + too + adj + (for somebody) + to + V <br>S + V + too + adv + (for somebody) + to + V","Dùng để phàn nàn quá mức để làm việc gì đó");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("36","Enough","S + V + adv + enough + (for somebody) + to + V <br>S + to be + adj + enough + (for somebody) + to + V","Để nói về mức độ vừa đủ");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("37","May/Might","S may/might + V-inf","May dùng để diễn tả khả năng xảy ra của sự việc có độ chắc chắn cao hơn 50% <br>Might có chức năng tương tự như may, tuy nhiên khả năng xảy ra của hành động có độ chắc chắn không cao, dưới 50% <br>Dùng để xin phép một cách lịch sự","Khi thời điểm nói trong quá khứ chỉ dùng might không dùng may");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("38","Should","S + “Should”/ “Should not”+ Verb","“should” mang tính gợi ý, biểu đạt quan điểm cá nhân. “Should” thường được dùng cho các câu phủ định và câu hỏi");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("38","Ought to","S + “Ought to”/ “Ought not to” + Verb","“ought to” là một lời khuyên mang tính chắc chắn hơn “should”, giải pháp này đưa ra khi không còn cách giải quyết khác. Ngoài ra, “ought to” còn nói về nhiệm vụ hoặc luật lệ bắt buộc phải làm");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("38","Had better","S + “had better”/ “had better not” + Verb","Nghĩa cũng tương tự về việc nên làm gì đó, có thể dùng để đưa ra lời khuyên");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("39","Model Perfect for deduction","May/might/Could + have + V-3: có thể nhưng không xảy ra <br>Can't/Couldn't + have + V-3: chắc chắn đã không xảy ra <br>Must + have + V-3: chắc chắn đã xảy ra","Phần nhiều dùng để nói hoặc suy đoán về những chuyện trong quá khứ tùy theo mức độ chắc chắn của người nói");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("40","Present Perfect Continous","Khẳng định: S + have/ has + been + V-ing <br>Phủ định: S + haven’t/ hasn’t + been + V-ing <br>Nghi vấn: Have/ Has + S + been + V-ing ?","Được sử dụng để chỉ sự việc xảy ra trong quá khứ nhưng vẫn còn tiếp tục ở hiện tại và có thể vẫn còn tiếp diễn trong tương lai. Chúng ta sử dụng thì này để nói về sự việc đã kết thúc nhưng chúng ta vẫn còn thấy ảnh hưởng");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("41","Present Perfect vs Past Simple","Present Perfect: S + have/has + V-3/ed <br>Past Simple: S + was/were + adj/N     S + V2-ed","Về căn bản cả 2 đều nói về chuyện của quá khứ nhưng thì quá khứ đơn nói về các chuyện đã kết thúc hẳn hoặc mang tính lịch sử <br>Trong khi đó hiện tại hoàn thành thường nói về việc xảy ra gần đây và không muốn đề cập đến thời gian xảy ra sự việc, thậm chí việc đó có thể xảy ra ở hiện tại");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("42","Bị động","S + be + V2-ed (+ by O)","Khi muốn câu văn phong phú hơn người nói tập trung vào đối tượng bị tác động nhiều hơn là chủ thể gây ra hành động đó");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("43","Quá khứ hoàn thành","Khẳng định: S + had + past participle <br>Phủ định: S + hadn’t + past participle <br>Từ để hỏi + had + S + past participle","Dùng để diễn tả một hành động xảy ra trước 1 hành động khác trong quá khứ. Hành động nào xảy ra trước sẽ dùng thì quá khứ hoàn thành. Hành động nào xảy ra sau sẽ dùng thì quá khứ đơn.");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("44","Câu hỏi đuôi","Phần đuôi phủ định: S + special verb….. , special verb + not + S? <br>Phần đuôi khẳng định: S + special verb + not….., special verb  + S?","Có một chú ý là nếu người hỏi xuống giọng ở cuối câu hỏi thì thực sự họ không muốn hỏi mà là đang trông chờ người ta đồng ý với điều mình nói. Khi lên giọng ở cuối câu hỏi thì mới là một câu hỏi thật sự và bạn muốn nhận được câu trả lời.");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("45","Should have","S + should have + V3-ed","Should have được dùng để nói về những sự việc đã không hoặc có thể đã không xảy ra trong quá khứ <br>Bày tỏ sự tiếc nuối với việc đã qua");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("46","Tương lai hoàn thành","Khẳng định: S + will + have + V-3 <br>Phủ định: S + will + not + have + V-3 <br>Nghi vấn: Will + S + have + V-3 ?","Thì tương hoàn thành (Future perfect) là một thì trong tiếng Anh hiện đại. được dùng để diễn tả hành động sẽ hoàn thành tới 1 thời điểm xác định trong tương lai");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("47","Tương lai hoàn thành tiếp diễn","Khẳng định: S + will + have + been +V-ing <br>Phủ định: S + will not/ won’t + have + been + V-ing <br>Nghi vấn: Will + S + have + been + V-ing?","Thì tương hoàn thành tiếp diễn (Future perfect continuous tense) là một thì trong tiếng Anh hiện đại. Được dùng để diễn tả một hành động đã đang xảy ra cho tới 1 thời điểm trong tương lai");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`,`Note`) VALUES ("48","Mệnh đề quan hệ","Mệnh đề chính, Mệnh đề quan hệ (chứa các đại từ quan hệ) <br>Who: nói về người làm chủ ngữ <br>Whom: nói về người làm tân ngữ <br>Whose: Chỉ sở hữu <br>Which: nói về vật","Mệnh đề quan hệ xác định (Defining relative clauses): là mệnh đề được dùng để xác định danh từ đứng trước nó. Mệnh đề xác định là mệnh đề cần thiết cho ý nghĩa của câu, không có nó câu sẽ không đủ nghĩa. Nó được sử dụng khi danh từ là danh từ không xác định và không dùng dấu phẩy ngăn cách nó với mệnh đề chính <br>Mệnh đề quan hệ không hạn định (Non-defining relative clauses): là mệnh đề cung cấp thêm thông tin về một người, một vật hoặc một sự việc đã được xác định. Mệnh đề không xác định là mệnh đề không nhất thiết phải có trong câu, không có nó câu vẫn đủ nghĩa. Nó được sử dụng khi danh từ là danh từ xác định và được ngăn cách với mệnh đề chính bằng một hoặc hai dấu phẩy (,) hay dấu gạch ngang (-)","That có thể dùng thay tất cả đại từ kia nhưng chỉ trong mệnh đề xác định");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("49","Quá khứ hoàn thành tiếp diễn","Khẳng định: S + had + been + V-ing <br>Phủ định: S + hadn’t + been + V-ing <br>Nghi vấn: Had + S + been + V-ing?  hoặc   Wh-questions + had + S + been + Ving…?","Trong tiếng Anh, thì quá khứ hoàn thành tiếp diễn (past perfect continuous) là 1 phần trong cấu trúc ngữ pháp tiếng Anh được dùng để diễn đạt quá trình xảy ra một hành động bắt đầu trước một hành động khác trong quá khứ.");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("50","Tường thuật dạng câu kể","S + say(s)/said + (that) + S + V (mệnh đề phụ lùi về một mốc thời gian tương lai -> hiện tại, hiện tại -> quá khứ)","Kể lại lời nói của người khác mà mình đã nghe được");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("50","Tường thuật dạng câu hỏi","es/No questions: S+asked/wanted to know/wondered+if/wether+S+V <br>Wh-questions: S + asked(+O)/wanted to know/wondered + Wh-words + S + V","Kể lại những câu hỏi mà mình được đặt ra");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("50","Tường thuật dạng câu mệnh lệnh"," S + told + O + (not) to-infinitive","Kể lại về những mệnh lệnh mình đã nghe hoặc đơn giản chỉ muốn tường thuật lại lời nói kém trang trọng");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("51","Used to","S + used to + V-inf","Thể hiện mình từng làm việc gì đó trong quá khứ nhưng giờ không còn nữa");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("51","Be used to","S + be used to + V-ing","Ta dùng cấu trúc này để diễn tả quá trình diễn ra của một hành động đang dần trở thành thói quen");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("51","Would","would + V-inf","Would cũng để nói về việc từng xảy ra thường xuyên thành thói quen trong quá khứ, nhưng không dùng để chỉ trạng thái");
INSERT INTO `gram_post_section` (`ID_topic`,`Title`,`Formular`,`Usage`) VALUES ("52","Wish/if only","Ước cho tương lai: S + wish(es) + (that) + S + would/could + V  <br>Ước cho hiện tại: S + wish(es) + (that) + S + V-ed <br>Ước cho quá khứ: S + wish(es) + (that) + S + had + V3","Thể hiện mong ước của bản thân. If only: ước gì, giá như");
